import React, {useEffect, useState} from 'react';
import logo from '../images/1920px-Netflix_2015_logo.svg.png';
import avatar from '../images/AAAABYnnca7HCf0z4YHtIK5R8MIGCeMyodAsxBYSBmMkYHqjSw46VWWyNQirfwxT-CkbxPkp-G84Wu-iOMwGG-r9QAs.png';
import './Nav.css'
const Nav = () =>{
    const [showBackground ,setShowBackground] = useState(false);
    useEffect(() => {
        window.addEventListener('scroll',()=>{
            if(window.scrollY > 100){
                setShowBackground(true);
            } else {
                setShowBackground(false)
            }
        })
        return ()=>window.removeEventListener('scroll');
    })
    return <div className = {`nav ${showBackground ? 'nav__colored' : ''}`}>
        <img className = 'nav__logo' src = {logo} alt = 'Netflix-Logo'/>
        <img className = 'nav__avatar' src ={avatar} alt = 'Netflix-Avatar'/>
    </div>
}

export default Nav;
