import React, {useState ,useEffect} from 'react'
import axios from '../../../axios';
import './Row.css';
import Youtube from 'react-youtube';
import movieTrailer from 'movie-trailer';

const Row = ({title ,urlRequest, isLarge}) => {

    const [movies, setMovies] = useState([]);
    const [trailerUrl, setTrailerUrl] = useState('');
    const baseURL = 'https://image.tmdb.org/t/p/original/';
    const opts = {
        height : '300px',
        width : '100%',
        playerVars : {
            autoPlay : 1,
        },
    };

    useEffect(()=>{
        axios.get(urlRequest)
        .then(res=>setMovies(res.data.results))
        .catch(error=>console.log(error))
    },[urlRequest]);

    const posterClicked = movie => {

        if(trailerUrl) {
            setTrailerUrl('');
        } else {
            movieTrailer(movie.name || movie.title || movie.original_title || movie.original_name ||'')
            .then(url => {
                const urlParams = new URLSearchParams(new URL(url).search);
                setTrailerUrl(urlParams.get('v'));
            })
            .catch(err => console.log(err));
        }

    }
    
    return <div className='row'>
        <h2 className='row__title'>{title}</h2>
        <div className='row__posters'>
            {movies.map(movie => {
                console.log(movies)
                if(movie.backdrop_path !== null){
                    return <div className='row__posterContainer'                         key={movie.id} 
                    >
                        <img src={`${baseURL}${isLarge ? movie.poster_path : movie.backdrop_path }`} onClick={() => posterClicked(movie)}
                        alt={movie.name}
                        className={`row__poster ${isLarge ? 'row__largePoster' : ''}`}
                        />
                        <div className='row__trailer'>{trailerUrl &&<Youtube videoId={trailerUrl} opts={opts}/>}</div> 

                    </div>
                }
            }   
            )}
        </div>
    </div>

}

export default Row;
