import * as actionTypes from './action';

const initialState = {
    user : null
};

const userReducer = (state = initialState, action) => {
    switch (action.type) {
        case actionTypes.LOGIN :
            return {
                ...state,
                user : action.user
            }
    }
    return state;
}

export default userReducer;
